import React from 'react';
import cn from 'classnames';

const sizes = {
  huge: 'text-4xl',
  big: 'text-2xl',
  normal: 'text-base',
};

const weights = {
  medium: 'font-medium',
  thin: 'font-thin',
  semibold: 'font-semibold'
}

const Title = ({ as: Html,Â children, size, uppercase, centered, weight }) => {
  return (
    <Html className={cn('my-1 pb-4 tracking-widest text-gray-600', weights[weight], sizes[size], { 'text-center': centered, uppercase })}>
      {children}
    </Html>
  )
};

Title.defaultProps = {
  size: 'normal',
  weight: 'medium'
};

export default Title;
