import App from 'next/app'
import React from 'react'
import { ThemeProvider } from 'styled-components';
import Menu from '../components/Menu';
import '../assets/index.css';

import { GlobalStyle } from '../components/Global';

const theme = {
  colors: {
    primary: '#0070f3',
  },
}
const menuConfig = [
  {
    href: '/',
    label: 'Accueil',
  },
  {
    href: '/projets',
    label: 'Projets',
  },
  {
    href: '#',
    label: 'Articles',
  },
  {
    href: '/contact',
    label: 'Contact',
  },
]
export class PortFolioApp extends App {
  render() {
    const { Component, pageProps } = this.props

    return (
      <ThemeProvider theme={theme}>

        <div className="container mx-auto">
          <Menu items={menuConfig} />
        </div>
        <GlobalStyle />
        <Component {...pageProps} />
        <div className="container mx-auto">
          <iframe src="https://snapwidget.com/embed/884278" class="snapwidget-widget" allowtransparency="true" frameborder="0" scrolling="no" style={{ border: 'none', overflow: 'hidden', width: '1280px', height: '160px'}} />
        </div>
      </ThemeProvider>
    )
  }
}

export default PortFolioApp;
