import React from 'react';
import cn from 'classnames';

const sizes = {
  huge: 'text-4xl',
  normal: 'text-base',
};

const Title = ({ as: Html,Â children, size, uppercase, centered }) => {
  return (
    <Html className={cn('my-1 tracking-widest font-thin text-gray-600', sizes[size], { 'text-center': centered, uppercase })}>
      {children}
    </Html>
  )
};

