import React from 'react'
import Head from 'next/head'
import { MissionCard } from '../components/Mission';
import { Container } from '../components/Layout';

import STACKS_LIST from '../constants/stacks';
import MISSIONS_LIST from '../constants/missions';
import { SectionHeader } from '../components/Title';
import ImageLayout from '../components/Layout/ImageLayout';
import Header from '../components/Header';
import StackCard from '../components/StackCard';
import ArticleCard from '../components/ArticleCard';

const menuConfig = [
  {
    href: '/',
    label: 'Accueil',
  },
  {
    href: '/projets',
    label: 'Projets',
  },
  {
    href: '#',
    label: 'Articles',
  },
  {
    href: '#',
    label: 'Contact',
  },
]

const Home = () => (
  <div>
    <Head>
      <title>Home</title>
      <link rel='icon' href='/favicon.ico' />
    </Head>
    <div className="container mx-auto">
      <Header items={menuConfig}/>

      <div class="py-12 bg-white">
        <h2 class="text-base text-center my-6 leading-6 text-indigo-600 font-semibold tracking-wide uppercase">Derniers articles</h2>
        <ArticleCard category="Mode" imageUrl="http://remi-mavillaz.fr/images/articles/openclassroom-meetup.png" title="Internet, une nouvelle forme d'apprentissage?" author="Rémi 'Kize' Mavillaz" date="Novembre 2016" description="Se cultiver, apprendre, échanger, et développer ses propres idées, c’est aujourd’hui possible très facilement grâce à internet. Ainsi, est-il possible d’apprendre sur internet, de la même manière que dans des établissements physique?" />
        <ArticleCard category="Mode" imageUrl="http://remi-mavillaz.fr/images/articles/openclassroom-meetup.png" title="Internet, une nouvelle forme d'apprentissage?" author="Rémi 'Kize' Mavillaz" date="Novembre 2016" description="Se cultiver, apprendre, échanger, et développer ses propres idées, c’est aujourd’hui possible très facilement grâce à internet. Ainsi, est-il possible d’apprendre sur internet, de la même manière que dans des établissements physique?" />
      </div>
        <h2 class="text-base text-center my-6 leading-6 text-indigo-600 font-semibold tracking-wide uppercase">Technos préférés</h2>
        <div class="grid grid-cols-6 gap-6">
          {STACKS_LIST.map(stack => <StackCard {...stack} />)}
        </div>
        <h2 class="text-base text-center my-6 leading-6 text-indigo-600 font-semibold tracking-wide uppercase">Biographie</h2>
        <div class="">
          <h3 class="mt-2 lg:text-center text-3xl leading-8 font-extrabold tracking-tight text-gray-900 sm:text-4xl sm:leading-10">
            Envie de savoir qui je suis?
          </h3>
          <p class="my-4 text-base leading-6 text-gray-500">
            Je m’appelle Rémi, j’ai 29 ans et je vis à Rueil-Malmaison dans les Hauts-de-Seine avec mon Shiba inu. <br />
            Passioné de jeux vidéos mais aussi par le code, je créé des applications web depuis quelques années.

            J’aime partager mes connaissances, et m’investir à fond dans les produits que je développe !
          </p>
        </div>
        <h2 class="text-base text-center my-6 leading-6 text-indigo-600 font-semibold tracking-wide uppercase">Experiences</h2>
        {MISSIONS_LIST.map(({ description1, description2, ...mission })=> (
          <MissionCard {...mission}>
            {description1}
            <br /><br />
            {description2}
          </MissionCard>
        ))}
    </div>
  </div>
)

export default Home
