import styled from 'styled-components';

const OverlayTitle = styled.span`
  font-family: SFProDisplayMedium;
  font-size: 4.5vw;
  color: #7586F8;
  text-transform: uppercase;
  opacity: 0.07;
  position: absolute;
  top: 0;
  left: 0;
  margin: 0;
`;

export default OverlayTitle;
