import styled from 'styled-components';

const SectionTitle = styled.h2`
  font-family: SFProDisplayMedium;
  font-size: 1.55vw;
  color: #252A3F;
  text-transform: uppercase;
  margin: 0;
  line-height: 7.2vw;
  position: absolute;
  top: 0;
  left: 0;
  margin: 0;
`;

export default SectionTitle;
